export default {
  name: 'DataFlow',
  props: {
    instanceKey: {
      defaul: ''
    },
    action: {
      defaul: 'view'
    },
    width: {
      type: [String, Number],
      default: 800
    },
    height: {
      type: [String, Number],
      default: 250
    },
    gridSize: {
      type: Number,
      default: 1
    },
    drawGrid: {
      type: [Object, Boolean],
      default: false
    },
    background: {
      type: [Object, Boolean],
      default: false
    },
    readonly: {
      type: Boolean,
      default: false
    },
    highLightLinks: {
      type: Array,

      default() {
        return [];
      }

    },
    nodes: {
      type: [Object, Boolean],
      default: false
    },
    links: {
      type: [Object, Boolean],
      default: false
    }
  },

  data() {
    return {
      stencilShapes: [],
      selectionNode: null,
      selectedElement: null,
      selectedLink: null,
      //check ctrl shift
      ctrlDown: false,
      shiftDown: false,
      color: "rgb(255 136 0)",
      arr: ['filter', 'formula', 'join', 'load', 'pivot', 'sample', 'script', 'select', 'sort', 'summarize', 'transposeTable', 'union', 'weightAvg']
    };
  },

  created() {
    this.graph = new joint.dia.Graph();
  },

  mounted() {
    this.initPaper();
  },

  methods: {
    initPaper() {
      let graph = this.graph;
      this.commandManager = new joint.dia.CommandManager({
        graph: graph,
        cmdBeforeAdd: (cmdName, cell, graph, options = {}) => {
          return !options.ignoreCommandManager;
        }
      });
      this.paper = new joint.dia.Paper({
        width: 1000,
        height: 800,
        model: this.graph,
        async: true,
        sorting: joint.dia.Paper.sorting.APPROX,
        gridSize: 10,
        drawGrid: true,
        restrictTranslate: true,
        stopDelegation: false,
        interactive: {
          vertexAdd: false
        },
        // background: { color: '#F3F7F6' },
        // defaultConnectionPoint: { name: 'boundary' },
        // defaultConnector: {
        //     name: "rounded"
        //   },
        //   defaultRouter: {
        //     name: "manhattan",
        //     args: {
        //       step: 10,
        //       endDirections: ["bottom"],
        //       startDirections: ["top"],
        //       padding: { bottom: 20 }
        //     }
        //   },
        snapLinks: {
          radius: 75
        },
        linkPinning: false,
        multiLinks: false,
        defaultRouter: {
          name: 'manhattan',
          args: {
            padding: 10
          }
        },
        defaultConnector: {
          name: 'rounded'
        },
        validateConnection: function (cellViewS, magnetS, cellViewT, magnetT, end, linkView) {
          // Prevent linking from input ports.
          if (magnetS && magnetS.getAttribute('port-group') === 'in') return false; // Prevent linking from output ports to input ports within one element.

          if (cellViewS === cellViewT) return false; // Prevent linking to input ports.

          return magnetT && magnetT.getAttribute('port-group') === 'in' || cellViewS.model.get('type') === 'qad.Question' && cellViewT.model.get('type') === 'qad.Answer';
        },
        // validateConnection: function (view1, _magnet1, view2, _magnet2) {
        //     // Do not allow loop links (Element to Link, Element A to Element B is valid).
        //     return view1 !== view2;
        // },
        interactive: {
          linkMove: true,
          labelMove: true,
          arrowheadMove: true,
          vertexMove: false,
          vertexAdd: false,
          vertexRemove: false,
          useLinkTools: false
        },
        defaultLink: function () {
          return new joint.shapes.standard.Link({
            attrs: {
              z: 1,
              line: {
                connection: true,
                stroke: '#707070',
                strokeLinejoin: 'round'
              },
              wrapper: {
                connection: true,
                strokeWidth: 10,
                strokeLinejoin: 'round'
              }
            },
            markup: [{
              tagName: 'path',
              selector: 'wrapper',
              attributes: {
                'fill': 'none',
                'stroke': 'transparent'
              }
            }, {
              tagName: 'path',
              selector: 'line',
              attributes: {
                'fill': 'none'
              }
            }]
          });
        }
      });
      this.paperScroller = new joint.ui.PaperScroller({
        paper: this.paper,
        autoResizePaper: true,
        scrollWhileDragging: true,
        cursor: 'grab'
      });
      this.selection = new joint.ui.Selection({
        theme: 'material',
        paper: this.paper,
        useModelGeometry: true,
        allowTranslate: true
      });
      this.snaplines = new joint.ui.Snaplines({
        paper: this.paper
      });
      this.snaplines.startListening(); //render paper

      document.getElementById('paper').appendChild(this.paperScroller.render().el); //render paperScroller

      this.highlighters = joint.highlighters;
      this.paperScroller.$el.css({
        width: 900,
        height: 600
      }).appendTo('#paper');
      this.paperScroller.center();
      this.addStencil();
      this.handlePaperEvents();
      this.selection.collection.reset(graph.getElements());
    },

    jsUcfirst(string) {
      return string.charAt(0).toUpperCase() + string.slice(1);
    },

    stencilRenderHandler(positionX, positionY, arr, elNoInPorts = "", spaceBetweenElToX = 0, spaceBetweenElToY = 0) {
      var customEl = joint.dia.Element.define('standard.Rectangle', {
        z: 1,
        size: {
          width: 44,
          height: 35
        },
        removable: false,
        attrs: {
          image: {
            x: 5,
            y: 5,
            width: 'calc(w-10)',
            height: 'calc(h-10 )'
          },
          root: {
            magnet: false
          },
          body: {
            fill: '#FFFFFF',
            stroke: '#A0A0A0',
            width: 'calc(w)',
            height: 'calc(h)',
            strokeWidth: 2,
            rx: 5,
            ry: 5
          }
        },
        ports: {
          items: [{
            id: 'out-port',
            group: 'out'
          }, {
            id: 'in-port',
            group: 'in'
          }],
          groups: {
            out: {
              markup: [{
                tagName: 'circle',
                selector: 'portBody'
              }],
              position: {
                name: 'right'
              },
              attrs: {
                portBody: {
                  r: 5,
                  magnet: 'active',
                  fill: '#187BD3',
                  stroke: '#187BD3'
                }
              }
            },
            in: {
              markup: [{
                tagName: 'circle',
                selector: 'portBody'
              }],
              position: {
                name: 'left'
              },
              attrs: {
                portBody: {
                  r: 5,
                  magnet: 'passive',
                  fill: '#FFFFFF',
                  stroke: '#187BD3'
                }
              }
            }
          }
        },
        markup: [{
          tagName: 'rect',
          selector: 'body'
        }, {
          tagName: 'image',
          selector: 'image'
        }, {
          tagName: 'ports',
          selector: 'ports'
        }]
      });
      var customElNoInPorts = joint.dia.Element.define('standard.Rectangle', {
        z: 1,
        theme: 'material',
        size: {
          width: 44,
          height: 35
        },
        removable: false,
        attrs: {
          image: {
            x: 5,
            y: 5,
            width: 'calc(w-10)',
            height: 'calc(h-10 )'
          },
          root: {
            magnet: false
          },
          body: {
            fill: '#FFFFFF',
            stroke: '#A0A0A0',
            width: 'calc(w)',
            height: 'calc(h)',
            strokeWidth: 2
          },
          line: {
            connection: true,
            stroke: '#333333',
            strokeWidth: 2,
            strokeLinejoin: 'round',
            targetMarker: {
              'type': 'path',
              'd': 'M 10 -5 0 0 10 5 z'
            }
          }
        },
        ports: {
          items: [{
            id: 'out-port',
            group: 'out'
          }],
          groups: {
            out: {
              markup: [{
                tagName: 'circle',
                selector: 'portBody'
              }],
              position: {
                name: 'right'
              },
              attrs: {
                portBody: {
                  r: 5,
                  magnet: 'active',
                  fill: '#187BD3',
                  stroke: '#187BD3'
                }
              }
            }
          }
        },
        markup: [{
          tagName: 'rect',
          selector: 'body'
        }, {
          tagName: 'image',
          selector: 'image'
        }, {
          tagName: 'ports',
          selector: 'ports'
        }]
      });
      this.arr.forEach(element => {
        //chuyển thành hoa chữ đầu cho đúng định dạng file ảnh lưu
        let elementName = this.jsUcfirst(element);

        if (elementName === this.jsUcfirst(elNoInPorts)) {
          element = new customElNoInPorts();
        } else {
          element = new customEl();
        }

        element.prop('attrs/image/xlinkHref', require(`../assets/${elementName}.svg`));
        this.stencilShapes.push(element.position(positionX, positionY));

        if (spaceBetweenElToX > 0) {
          positionX += element.attributes.size.width + spaceBetweenElToX;
        }

        if (spaceBetweenElToY > 0) {
          positionY += element.attributes.size.height + spaceBetweenElToY;
        }
      });
    },

    addStencil() {
      let graph = this.graph;
      let paper = this.paper;
      var stencil = new joint.ui.Stencil({
        paper: this.paperScroller,
        width: 900,
        height: 60,
        dropAnimation: {
          duration: 200,
          easing: 'swing'
        }
      });
      this.stencilRenderHandler(20, 20, this.arr, "load", 16);
      document.getElementById('stencil').appendChild(stencil.render().el);
      stencil.load(this.stencilShapes);
    },

    getElementPredecessorLinks(el) {
      return graph.getSubgraph([el, ...graph.getPredecessors(el)]).filter(cell => cell.isLink());
    },

    highlightCell(cell) {
      let self = this;

      if (cell) {
        self.highlighters.addClass.add(cell.findView(paper), cell.isElement() ? "body" : "line", "selection", {
          className: "selection"
        });
      } else {
        return;
      }
    },

    unhighlightCell(cell) {
      let self = this;

      if (cell) {
        self.highlighters.addClass.remove(cell.findView(paper), "selection");
      } else {
        return;
      }
    },

    selectElement(el) {
      let self = this;
      let graph = self.graph;
      if (self.selectionNode === el) return;

      if (self.selectionNode) {
        unhighlightCell(self.selectionNode);
        graph.getLinks().forEach(link => unhighlightCell(link));
      }

      if (el) {
        highlightCell(el);
        getElementPredecessorLinks(el).forEach(link => {
          highlightCell(link);
          link.toFront({
            ignoreCommandManager: true
          });
        });
        self.selectionNode = el;
      } else {
        self.selectionNode = null;
      }
    },

    addHalo(cellView) {
      var halo = new joint.ui.Halo({
        cellView: cellView,
        type: 'toolbar',
        boxContent: false // bang phia duoi cell cho biet tọa độ height width và angle

      });
      halo.removeHandle('resize');
      halo.removeHandle('rotate');
      halo.removeHandle('direction');
      halo.render();
    },

    handlePaperEvents() {
      let self = this;
      let paper = self.paper;
      let graph = self.graph; //nếu bấm thì =true

      document.addEventListener('keydown', function (evt) {
        if (evt.code === "ControlLeft" || evt.code === "ControlRight") {
          self.ctrlDown = true;
        }

        if (evt.code === "ShiftLeft" || evt.code === "ShiftRight") {
          self.shiftDown = true;
        }
      }); //nếu thả nút thì =false

      document.addEventListener('keyup', function (evt) {
        if (evt.code === "ShiftLeft" || evt.code === "ShiftRight") {
          self.shiftDown = false;
        }

        if (evt.code === "ControlLeft" || evt.code === "ControlRight") {
          self.ctrlDown = false;
        }
      });
      paper.on('cell:pointerup', function (cellView) {
        self.addHalo(cellView);

        if (cellView.model.attributes['type'] === 'standard.Link') {
          self.$emit('select-link', {
            id: cellView.model.id
          });
        } else {
          self.$emit('select-cell', {
            id: cellView.model.id
          });
        }

        self.selectedElement = cellView.model;
        self.selectedElement.toFront({
          ignoreCommandManager: true
        }); // cellView.$el[0].parentElement.appendChild(cellView.$el[0])
        // console.log(graph.getLinks())
      });
      paper.on({
        //xử lý khi các sự kiện 
        "cell:mouseenter": cellView => {
          cellView.model.toFront({
            ignoreCommandManager: true
          });
        },
        "cell:mouseleave": cellView => {
          cellView.model.toBack({
            ignoreCommandManager: true
          });

          if (self.selectionNode) {
            getElementPredecessorLinks(self.selectionNode).forEach(link => {
              link.toFront({
                ignoreCommandManager: true
              });
            });
          }
        },
        "element:pointerclick": cellView => {
          //chỉ đổi màu khi ko bấm ctrl
          if (self.ctrlDown === false) {// selectElement(cellView.model)
          }
        },
        "link:pointerclick": cellView => {// selectElement(cellView.model)
          //chon vao link thi mo cho phep doi node
          // cellView.addTools(toolsView);
        },
        //chuyển lại màu cũ khi bấm ra ngoài
        "blank:pointerclick": () => {
          // selectElement(null)
          paper.removeTools({
            ignoreCommandManager: true
          });
        },
        'element:pointerup': function (cellView, evt) {
          console.log(evt);

          if (evt.ctrlKey || evt.metaKey) {
            console.log(self.selection);
            self.selection.collection.add(cellView.model); //loop to all the element in selection and set it to front

            self.selection.collection.models.forEach(element => {
              element.toFront({
                ignoreCommandManager: true
              });
            });
          }
        },
        //chuyển màu khi bấm vào link và cho nó lên trc
        'link:pointerup': function (cellView) {
          self.selectedLink = cellView.model;
          self.selectedLink.toFront({
            ignoreCommandManager: true
          });
        },
        // lắng nghe sự kiện bấm ở ngoài khoange trống check shift để active select
        'blank:pointerdown': function (evt, x, y) {
          if (self.shiftDown === true) {
            // selectElement(null)
            self.paperScroller.setCursor('crosshair');
            self.selection.startSelecting(evt);
          } else {
            console.log(self.getAllNodes());
            console.log(self.getAllLinks());
            self.selection.collection.reset([]);
            self.paperScroller.startPanning(evt, x, y);
            paper.removeTools();
            self.paperScroller.setCursor('grab');
          }
        }
      }); //xóa el nếu chọn lại lần 2 khi bấm ctrl

      self.selection.on('selection-box:pointerdown', function (cellView, evt) {
        if (evt.ctrlKey || evt.metaKey) {
          self.selection.collection.remove(cellView.model);
        }
      }); //kết hợp bấm ctrl để check các tổ hợp phím ctrl + C,V,Z,Y,A...

      document.addEventListener('keydown', function (evt) {
        self.clipboard = new joint.ui.Clipboard();

        if (evt.code === "NumpadAdd" && self.shiftDown === true) {
          // console.log(evt.code)
          self.paperScroller.zoom(0.2, {
            max: 4
          });
        }

        if (evt.code === "NumpadSubtract" && shiftDown === true) {
          self.paperScroller.zoom(-0.2, {
            min: 0.2
          }); // console.log(evt.code)
        } //check+ A


        if (evt.code === "KeyA" && self.ctrlDown === true) {
          self.selection.collection.reset(graph.getElements());
          evt.preventDefault();
        } //check ctrl + Z


        if (evt.code === "KeyZ" && self.ctrlDown === true) {
          self.commandManager.undo(); // console.log(selectionNode)

          unhighlightCell(self.selectionNode);
          graph.getLinks().forEach(link => unhighlightCell(link)); // console.log(selectionNode)

          self.selectionNode = null;
        } //check ctrl + Y


        if (evt.code === "KeyY" && self.ctrlDown === true) {
          self.commandManager.redo(); // selectElement(null)
        } //check ctrl + V ,ctrl + C, ctrl+X


        if (evt.code === "KeyX" && self.ctrlDown === true) {
          self.clipboard.cutElements(self.selection.collection, paper.model);
        }

        if (evt.code === "KeyC" && self.ctrlDown === true) {
          self.clipboard.copyElements(self.selection.collection, graph);
        }

        if (evt.code === "KeyV" && self.ctrlDown === true) {
          var pastedCells = self.clipboard.pasteCells(graph, {
            translate: {
              dx: 40,
              dy: 40
            },
            useLocalStorage: true
          });

          var elements = _.filter(pastedCells, function (cell) {
            return cell.isElement();
          }); // Make sure pasted elements get selected immediately. This makes the UX better as
          // the user can immediately manipulate the pasted elements.


          self.selection.collection.reset(elements);
        }
      });
    },

    getAllLinks() {
      return this.graph.getLinks();
    },

    getAllNodes() {
      return this.graph.getCells();
    },

    clearAll() {
      return this.graph.clear();
    },

    getNodeAttr(id) {
      let node = this.graph.getCell(id);

      if (node) {
        return node.attributes;
      } else {
        return {};
      }
    },

    render() {
      let nodeInfo = this.nodes;
      let linkInfo = this.links;
      let nodeType = nodeInfo.nodeType; //checknode

      if (nodeType) {
        let cell = new joint.shapes.nodeType({
          position: nodeInfo.position,
          id: nodeInfo.id
        });
        this.graph.addCell(cell);
      } else {
        console.error("Node type '" + nodeType + "' is not defined");
      }

      if (linkInfo) {
        let link = new joint.shapes.standard.Link();
        link.source(linkInfo.source);
        link.target(linkInfo.target);
        link.addTo(this.graph);
      }
    }

  }
};
